CREATE TABLE "public"."comments" ("pk" serial NOT NULL, "title" text NOT NULL, "comment" text NOT NULL, "novel_pk" integer NOT NULL, "user_pk" integer NOT NULL, PRIMARY KEY ("pk") , FOREIGN KEY ("user_pk") REFERENCES "public"."users"("pk") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("novel_pk") REFERENCES "public"."novels"("pk") ON UPDATE restrict ON DELETE restrict, UNIQUE ("pk"));COMMENT ON TABLE "public"."comments" IS E'comments';
