CREATE TABLE "public"."edited_novels_comments" ("pk" serial NOT NULL, "title" text NOT NULL, "comment" text NOT NULL, "user_pk" integer NOT NULL, "user_id" text NOT NULL, "edited_novel_pk" integer NOT NULL, "created_at" date NOT NULL DEFAULT now(), PRIMARY KEY ("pk") , FOREIGN KEY ("edited_novel_pk") REFERENCES "public"."edited_novels"("pk") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("user_pk") REFERENCES "public"."users"("pk") ON UPDATE restrict ON DELETE restrict);COMMENT ON TABLE "public"."edited_novels_comments" IS E'edited novels comment';
